import React, { useState } from 'react';
import VARS from '../Utility/VARS';

import AspectRatio from '@mui/joy/AspectRatio';
import Box from '@mui/joy/Box';
import Button from '@mui/joy/Button';
import Card from '@mui/joy/Card';
import IconButton from '@mui/joy/IconButton';
import Typography from '@mui/joy/Typography';

import BiggerScreenDevices from './BiggerScreenDevices'
import BigScreenDevices from './BigScreenDevices'
import MobileDevices from './MobileDevices'
import TabletDevices from './TabletDevices'

const CakeTab = (props) => {

    const [show, setShow] = useState(false);
    const [activeObj, setActiveObj] = useState(null)
    const [counterValue, setCounterValue] = useState(0)
    const [stockLimit, setStockLimit] = useState(100)
    const handleDecrement = () => setCounterValue(counterValue > 0 ? counterValue-1 : 0)
    const handleIncrement = () => setCounterValue(counterValue === stockLimit ? counterValue : counterValue+1)
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    
    const store = [
        {
            id: 0,
            type: "cake",
            name: "Blueberry Cheesecake",
            price: {
                no_bundle: 1499.00,
                with_bundle: null
            },
            defaultCurrency: "PHP",
            productDescription: "This rich, velvety, and luscious cheesecake is studded with blueberries that burst with sweetness in every bite.",
            imageSource: "p1_blueberry.jpg",
            storeStock: 100
        },
        {
            id: 1,
            type: "bread&pastries",
            name: "Filled Croissant",
            price: {
                no_bundle: null,
                with_bundle: {
                    box_with_6: 90.00,
                    box_with_12: 160.00
                }
            },
            defaultCurrency: "PHP",
            productDescription: "A rich, buttery, crescent-shaped roll of leavened dough with its rich flavorful fillings!",
            imageSource: "p12_croissant.webp",
            storeStock: 100
        },
        {
            id: 2,
            type: "cake",
            name: "Redvelvet Cake",
            price: {
                no_bundle: 1299.00,
                with_bundle: null
            },
            defaultCurrency: "PHP",
            productDescription: "Super moist, buttery, tender and soft cake with the most perfect red velvet flavor. Topped with easy cream cheese frosting.",
            imageSource: "p1_blueberry.jpg",
            storeStock: 100
        },
        {
            id: 3,
            type: "cake",
            name: "Strawberry Shortcake",
            price: {
                no_bundle: 1699.00,
                with_bundle: null
            },
            defaultCurrency: "PHP",
            productDescription: "A tender vanilla cake filled with layers of whipped cream frosting and juicy strawberries",
