        <>
            <BiggerScreenDevices>{forBigScreen()}</BiggerScreenDevices>
            <BigScreenDevices>{forBigScreen()}</BigScreenDevices>
            <TabletDevices>{forTabletScreen()}</TabletDevices>
            <MobileDevices>{forMobileScreen()}</MobileDevices>
        </>
        
    )
}

